CREATE TABLE INTERNAUTE (ID BIGINT AUTO_INCREMENT NOT NULL, ADRESSE VARCHAR(255), EMAIL VARCHAR(255), MOTDEPASSE VARCHAR(255), NOM VARCHAR(255), TELEPHONE VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE CATEGORIE (ID BIGINT AUTO_INCREMENT NOT NULL, DESCRIPTION VARCHAR(255), NOM VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE PRODUIT (ID BIGINT AUTO_INCREMENT NOT NULL, DESCRIPTION VARCHAR(255), IMAGE VARCHAR(255), NOM VARCHAR(255), PRIX DOUBLE, STOCK INTEGER, categorie_id BIGINT, PRIMARY KEY (ID))
CREATE TABLE PANIER (ID BIGINT AUTO_INCREMENT NOT NULL, internaute_id BIGINT, PRIMARY KEY (ID))
CREATE TABLE LIGNEPANIER (ID BIGINT AUTO_INCREMENT NOT NULL, QUANTITE INTEGER, commande_id BIGINT, panier_id BIGINT, produit_id BIGINT, PRIMARY KEY (ID))
CREATE TABLE COMMANDE (ID BIGINT AUTO_INCREMENT NOT NULL, ADRESSELIVRAISON VARCHAR(255), DATECREATION DATETIME, STATUT VARCHAR(255), internaute_id BIGINT, PRIMARY KEY (ID))
ALTER TABLE PRODUIT ADD CONSTRAINT FK_PRODUIT_categorie_id FOREIGN KEY (categorie_id) REFERENCES CATEGORIE (ID)
ALTER TABLE PANIER ADD CONSTRAINT FK_PANIER_internaute_id FOREIGN KEY (internaute_id) REFERENCES INTERNAUTE (ID)
ALTER TABLE LIGNEPANIER ADD CONSTRAINT FK_LIGNEPANIER_produit_id FOREIGN KEY (produit_id) REFERENCES PRODUIT (ID)
ALTER TABLE LIGNEPANIER ADD CONSTRAINT FK_LIGNEPANIER_commande_id FOREIGN KEY (commande_id) REFERENCES COMMANDE (ID)
ALTER TABLE LIGNEPANIER ADD CONSTRAINT FK_LIGNEPANIER_panier_id FOREIGN KEY (panier_id) REFERENCES PANIER (ID)
ALTER TABLE COMMANDE ADD CONSTRAINT FK_COMMANDE_internaute_id FOREIGN KEY (internaute_id) REFERENCES INTERNAUTE (ID)
